<?php$name = $price = $image='';$con = require "database.php";if ($_SERVER['REQUEST_METHOD']=='POST'){    $name = $_POST['name'];    $price = $_POST['price'];    $target_dir = "uploads/";    $target_file = $target_dir.basename($_FILES["image"]["name"]);    $uploadOk = 1;    $imageFileType = strtolower(pathinfo($target_file,PATHINFO_EXTENSION));// Check if image file is a actual image or fake image// Allow certain file formats    if($imageFileType != "jpg" && $imageFileType != "png" && $imageFileType != "jpeg"        && $imageFileType != "gif" ) {        echo "Sorry, only JPG, JPEG, PNG & GIF files are allowed.";        $uploadOk = 0;    }// Check if $uploadOk is set to 0 by an error    if ($uploadOk == 0) {        echo "Sorry, your file was not uploaded.";// if everything is ok, try to upload file    } else {        if (move_uploaded_file($_FILES["image"]["tmp_name"], $target_file)) {            echo "The file ". htmlspecialchars( basename( $_FILES["image"]["name"])). " has been uploaded.";            $image = $target_dir.basename( $_FILES["image"]["name"]);        } else {            echo "Sorry, there was an error uploading your file.";        }    }    $sql = "insert into product(name,price,image) values('$name',$price,'$image')";    $result = $con->query($sql);}$sql_show ="select *from product";$res = $con->query($sql_show);$a = [];$i = 0;if ($res->num_rows>0){    while ($row = $res->fetch_assoc()){        $a[$i++] = $row;    }}$con->close();include  "header.php"?><div class="container">    <div class="row">        <div class="col-md-8 offset-2">            <form action="<?php echo $_SERVER["PHP_SELF"]; ?>" method="post" enctype="multipart/form-data">                <div class="form-group">                    <label for="">name</label>                    <input type="text" class="form-control" name="name">                </div>                <div class="form-group">                    <label for="">price</label>                    <input type="text" class="form-control" name="price">                </div>                <div class="form-group">                    <label for="">image</label>                    <input type="file" class="form-control" name="image">                </div>                <input type="submit" value="submit" class="btn btn-success">            </form>        </div>    </div>    <div class="row">        <div class="col-md-10">              <table class="table table-bordered">                    <tr>                       <th>id</th> <th>name</th> <th>price</th> <th>action</th>                    </tr>                  <?php                      foreach ($a as $item){                          echo "<tr>";                          echo "<td>"  ,$item['id'],"</td>","<td>"  ,$item['name'],"</td>","<td>" ,$item['price'],"</td>","<td><img width='100' src='".$item['image']."' alt=''> </td>"                          , "<td><a href='action/delete_product.php?id=".$item['id']."' class='btn btn-danger'>delete</a>"                          ,"<a href='edit/edit_product.php?id=".$item['id']."' class='btn btn-info'>edit</a>"                          , "<a href='edit/show.php?id=".$item['id']."' class='btn btn-success'>show</a> </td>";                          echo "</tr>";                      }                  ?>              </table>        </div>    </div></div></body></html>