<?php   $id = $name = $price = $image = '';   $con = require '../database.php';   if ($_SERVER['REQUEST_METHOD']=='POST'){       $id = $_POST['id'];       $name = $_POST['name'];       $price = $_POST['price'];       $target_dir = "../uploads/";       $target_file = $target_dir.basename($_FILES["image"]["name"]);       $uploadOk = 1;       $imageFileType = strtolower(pathinfo($target_file,PATHINFO_EXTENSION));// Allow certain file formats       if($imageFileType != "jpg" && $imageFileType != "png" && $imageFileType != "jpeg"           && $imageFileType != "gif" ) {           echo "Sorry, only JPG, JPEG, PNG & GIF files are allowed.";           $uploadOk = 0;       }// Check if $uploadOk is set to 0 by an error       if ($uploadOk == 0) {           echo "Sorry, your file was not uploaded.";// if everything is ok, try to upload file       } else {           if (move_uploaded_file($_FILES["image"]["tmp_name"], $target_file)) {               echo "The file ". htmlspecialchars( basename( $_FILES["image"]["name"])). " has been uploaded.";               $image = $target_dir.basename( $_FILES["image"]["name"]);           } else {               echo "Sorry, there was an error uploading your file.";           }       }       if ($image!=null){           $sql = "update product set name = '$name', price = $price , image = '$image' where id = $id";       }else{           $sql = "update product set name = '$name', price = $price  where  id = $id";       }       $result = $con->query($sql);       if ($result){           header("location:../admin.php");       }else{           echo "error";       }   }?>